const longestPolindrom = s => {
    let res = '';
    let str = '';
    let strReverse = '';
    for (let i = 0; i < s.length; i++) {
        str = s[i];
        strReverse = s[i];
        for (let j = i + 1; j <= s.length; j++) {
            if (!res.length) res = str;
            str += s[j];
            strReverse = str[str.length - 1] + strReverse;
            if (str == strReverse && str.length > res.length) res = str;
        }
    }
    return res;
};

